import React from 'react'

const reviews = [
  {
    id: 1,
    name: 'Aisha',
    text: 'Lovely cafe — the latte was perfect and the ambiance is calming.',
  },
  {
    id: 2,
    name: 'Rafi',
    text: 'Best cold brew in town. Friendly staff and quick service.',
  },
  {
    id: 3,
    name: 'Mina',
    text: 'Cozy spot to work — reliable Wi-Fi and great pastries.',
  },
]

export default function Testimonials() {
  return (
    <section id="testimonials" className="py-16">
      <div className="max-w-6xl mx-auto px-4">
        <h3 className="text-3xl font-extrabold mb-6">What Customers Say</h3>
        <div className="grid md:grid-cols-3 gap-6">
          {reviews.map((r) => (
            <div
              key={r.id}
              className="bg-white dark:bg-gray-800 rounded-2xl p-6 shadow"
            >
              <div className="font-semibold">{r.name}</div>
              <div className="mt-2 text-gray-600 dark:text-gray-300">
                "{r.text}"
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}
