import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Heart } from 'lucide-react'
import { toggleFavorite } from '../redux/favoritesSlice'

export default function MenuCard({ item }) {
  const dispatch = useDispatch()
  const favorites = useSelector((s) => s.favorites.items)
  const favored = favorites.includes(item.id)

  const img = `https://images.unsplash.com/featured-?${encodeURIComponent(
    item.name
  )}&w=800&q=60`
  // Unsplash random image by name (works without API using unsplash source)
  const imgSrc = `https://source.unsplash.com/collection/coffee/800x600?${encodeURIComponent(
    item.name
  )}`

  return (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow p-4 flex flex-col transition-transform hover:scale-[1.01]">
      <div className="rounded-lg overflow-hidden h-40">
        <img
          src={imgSrc}
          alt={item.name}
          className="w-full h-full object-cover"
        />
      </div>
      <div className="mt-3 flex items-center justify-between">
        <div>
          <div className="font-semibold">{item.name}</div>
          <div className="text-sm text-gray-500 dark:text-gray-400">
            {item.category} â€¢ {item.price}
          </div>
        </div>
        <button
          onClick={() => dispatch(toggleFavorite(item.id))}
          aria-label="Toggle favorite"
          className={`p-2 rounded-md transition ${
            favored ? 'text-amber-500' : 'text-gray-400 dark:text-gray-300'
          }`}
        >
          <Heart className="" />
        </button>
      </div>
    </div>
  )
}
