import React from 'react'

export default function Hero() {
  return (
    <section
      id="hero"
      className="relative pt-24 bg-gradient-to-r from-yellow-50 to-amber-50 dark:from-gray-900 dark:to-gray-800 overflow-hidden"
    >
      {/* Decorative shapes */}
      <div className="absolute -top-32 -left-32 w-96 h-96 bg-amber-200 rounded-full opacity-30 blur-3xl dark:bg-yellow-700"></div>
      <div className="absolute -bottom-32 -right-32 w-96 h-96 bg-amber-300 rounded-full opacity-30 blur-3xl dark:bg-yellow-800"></div>

      <div className="max-w-6xl mx-auto px-6 py-24 grid grid-cols-1 md:grid-cols-2 gap-12 items-center relative z-10">
        {/* Text Content */}
        <div className="space-y-6">
          <h1 className="text-5xl md:text-6xl font-extrabold leading-tight bg-clip-text text-transparent bg-gradient-to-r from-amber-500 to-yellow-600 animate-fadeIn">
            CaféCanvas
          </h1>
          <p className="text-lg md:text-xl text-gray-700 dark:text-gray-300 animate-fadeIn delay-200">
            Wake up to the aroma — specialty brews made with care. Cozy space,
            local pastries, friendly baristas.
          </p>
          <div className="flex gap-4 mt-6 animate-fadeIn delay-400">
            <a
              href="#menu"
              className="px-6 py-3 bg-amber-500 text-white rounded-lg shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
            >
              See Menu
            </a>
            <a
              href="#contact"
              className="px-6 py-3 border-2 border-amber-400 text-amber-600 rounded-lg hover:bg-amber-50 dark:hover:bg-gray-700 transition"
            >
              Contact
            </a>
          </div>
        </div>

        {/* Hero Image */}
        <div className="relative group rounded-3xl overflow-hidden shadow-2xl">
          <img
            src="https://images.unsplash.com/photo-1509042239860-f550ce710b93?w=1200&q=80"
            alt="coffee"
            className="w-full h-80 md:h-full object-cover transform transition-transform duration-500 group-hover:scale-105"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/20 via-transparent to-black/20 opacity-0 group-hover:opacity-25 transition-opacity duration-500 rounded-3xl"></div>
        </div>
      </div>

      {/* Animation keyframes (Tailwind CSS plugin or extend via CSS) */}
      <style>
        {`
          @keyframes fadeIn {
            0% {opacity: 0; transform: translateY(20px);}
            100% {opacity: 1; transform: translateY(0);}
          }
          .animate-fadeIn {
            animation: fadeIn 1s ease forwards;
          }
          .delay-200 { animation-delay: 0.2s; }
          .delay-400 { animation-delay: 0.4s; }
        `}
      </style>
    </section>
  )
}
